INSERT INTO `bloques` (`bloque_id`, `seccion_id`, `texto1`, `texto2`, `imagen`, `formato_id`, `estado`) VALUES
(6, 1, 'Presentación', '<p>\n	Somos una empresa de Tecnolog&iacute;a con 20 a&ntilde;os de experiencia en el desarrollo, de aplicaciones, sitios web, e implementaci&oacute;n de software</p>\n', '', 8, 1),
(7, 1, 'Clientes', '<p>\n	Algunos de los que conf&iacute;an en nuestro servicio</p>\n', '', 8, 1),
(8, 3, 'Carniceria Boutique Vittelo', 'La mejor carne de zona..', '971d5-slidecarne.jpg', 3, 1),
(9, 3, 'Ofertas de la semana', '', '', 12, 1),
(10, 4, 'Bienvenidos', '<p>\n	Claudia Hasanbegovic es Doctora en Pol�ticas Sociales (Ph.D.) por la Universidad de Kent en Canterbury, Inglaterra; Master of Arts, por el Instituto de Estudios Sociales, en Holanda, Diplomada por IFU, Universidad Internacional de la Mujer, Alemania, Abogada por la Universidad Nacional de Lomas de Zamora, Argentina, y Docente universitaria graduada en el sistema SEDA e ILT de la UKC. La Dra. Hasanbegovic se desempe�a como Consultora Internacional, ejerciendo la Abogac�a y Asesorando a legisladoras(es) y ONGs. Ejerce la docencia universitaria de grado, postgrado y doctorado en la Argentina (UNSAM, UBA, UNLZ, etc.) e invitada por universidades extranjeras (Middlesex, Sussex; Universit� degli Studi di Messina; Universidad de Arte y Ciencias de Chiapas, etc.). Entre su experiencia profesional destaca el haberse desempe�ado como Directora de Relaciones Institucionales, Unidad de Informaci�n Financiera, Ministerio de Justicia de la Naci�n Argentina, haber ejercido el cargo de Prosecretaria Letrada de la Corte Suprema de Justicia de la Naci�n, nombrada por concurso en 2008, haber Coordinado Proyecto en Latin American Women�s Rights Service, en Londres, y haberse desempe�ado com Profesora de Pol�ticas Sociales en la Universidad de Kent en Canterbury, Gran Breta�a, etc.</p>\n', '', 3, 1),
(11, 0, '', '', '', 0, 0),
(12, 4, 'Consultoría y docencia', '<p>\n	La Doctora Hasanbegovic es consultora y docente. Como consultora ha desarrollado programas de capacitaci&oacute;n confeccionados a medida y pedidos por ONGs y organismos gubernamentales, y ha realizado trabajos de investigaci&oacute;n, elaborado y asesorado en proyectos de ley a comisiones legislativas. Asimismo, se form&oacute; como docente universitaria en la Universidad de Kent en Canterbury (Gran Breta&ntilde;a), y desde entonces ha estado ejerciendo la docencia, tanto en Argentina como en el extranjero.</p>\n<p>\n	En esta secci&oacute;n, se indica parte de este recorrido en ambas &aacute;reas mencionadas.</p>\n', '', 34, 1),
(13, 0, '', '', '', 0, 0);

